var xhr = new XMLHttpRequest();
var url = "http://52.17.209.211/api/referral";

var newData = JSON.parse(JSON.stringify(mdm.store));
delete newData.data__;
mdm.store.outbox = newData;

var data = {
    "apiKey": "pOicEkNGNFqiwWkJLWDfwsXODFhnfb47",
    "data": JSON.stringify(mdm.store.outbox)
};
xhr.open("POST", url, true);

xhr.setRequestHeader("Content-type", "application/json");

xhr.onreadystatechange = function() {
  	console.log("ReadyState: " + xhr.readyState);

    // If we have state 4 and a 200 or 201 the submission has been sent to the DB
    // We then update the message on screen and clear the data object for the new entry
    if (xhr.readyState == 4 && (xhr.status == 200 || xhr.status == 201)) {

        document.dispatchEvent(new CustomEvent('StoreHasChanged'));
        mdm.store.data__.variables.SubmissionProgress = "Submission Complete";

        var data = null;
        data = JSON.parse(JSON.stringify(mdm.store.data__));

        mdm.store = {
            "data__": data
        };

        mdm.ReloadApplication();

    // If we have state 1 it means a connection has been made
    } else if(xhr.readyState == 1){
        document.dispatchEvent(new CustomEvent('StoreHasChanged'));
        mdm.store.data__.variables.SubmissionProgress = "Submission In Progress";

    }else{
      document.dispatchEvent(new CustomEvent('StoreHasChanged'));
      mdm.store.data__.variables.SubmissionProgress = "Submission Failed";
    }

    mdm.store.data__.variables.loadingAPIData = false;
}
mdm.store.data__.variables.loadingAPIData = true;
xhr.send(JSON.stringify(data));

